import React from 'react';
// import {blogIndexMeta} from '../../blog/[slug]'//parses metadata from markdown
import PostMeta from './test';
import fs from 'fs';

export default function BlogPostLink ({htmlString, data}) {

    return(
        <>

                <title>{data.title}</title>
                <meta title="description" content={data.description}/>

            <div className="blog">
                <div dangerouslySetInnerHTML={{ __html: htmlString }} />
            </div>
            <div>
                {data.description}
            </div>
        </>
)};

export const getStaticPaths = async () => {

    const posts = fs.readdirSync('posts')

    const paths = posts.map(post => ({
        params: {
            slug: post.replace(".md", "")
        }
    }))

    return {
        paths,
        fallback: false
    }
}

export const getStaticProps = async ({params: {slug}}) => {

    const markdownWithMeta = fs.readFileSync(path.join('posts', slug+'.md')).toString();

    const parsedMarkdown = matter(markdownWithMeta) // meta is also known as 'frontmatter'

    const htmlString = marked(parsedMarkdown.content)

    return {
        props: {
            htmlString,
            data: parsedMarkdown.data
        }
    }
}

