import Link from 'next/Link';
import fs from 'fs';
import Layout from './components/Layout';
import BlogPostLink from './components/smallComponents/blogPostLink'
import matter from 'gray-matter'; 
import path from 'path';

const Blog = ({slugs, postsMeta}) => {
  return (
    <div>
    {console.log(slugs)}
      <ul>
        {slugs.map( slug => {
          return(
          <li key={slug}>
            <Link as={'/blog/'+slug} href={'/blog/'+slug}>
              <a>{slug}</a>
            </Link>
          </li>
          )
        })}
      </ul>

      {/* <div>
        {slugs.map( slug => {
          return(
            <BlogPostLink slug={slug} key={slug}/>
          )
        })}
      </div> */}

    </div> 
  )
}

export default Layout(Blog) // This adds the global layout (header & footer ect)

export const getStaticProps = async () => {
  const files = fs.readdirSync('posts')

  const posts = [];

  files.forEach(file => {
    posts.push(fs.readfileSync(path.join('posts', file)).toString())
  })

  // const postsMeta = [];

  // posts.map(post => {
  //   matter(post.toString())
  // })

  return {
    props: { 
      slugs: files.map(filename => filename.replace('.md','')),
      postsMeta: posts
    }
  }
}

