import React from 'react';
import fs from 'fs';

const Post = ({slug}) => {
    return <div>The slug for this page is {slug}</div>;
};

export const getStaticPaths = async () => {

    const files = fs.readdirSync('posts')
    console.log('files: ', files)

    const paths = files.map(filename => ({
        params: {
            slug: filename.replace(".md", "")
            //slug: filename.slice(0, filename.length - 3)
        }
    }))
    console.log('paths: ', paths)

    return {
        paths,
        fallback: false
    }
}
// this is a new SSG function, it create the path

export const getStaticPath = async ({params: {slug}}) => {
    return {
        props: {
            slug
        }
    }
}
// this function fetches the data to create the path

