import Modal from "../../smallComponents/Modal";

export default function CodePen() {

  function iFrame (title, url) { return <iframe 
    style={{width: '100%'}} 
    scrolling="no" 
    title={title} 
    src={url}
    loading="lazy" 
    allowTransparency="true" 
    allowFullScreen="true" 
    height={520} 
    frameBorder="no">
      See the Pen &lt;a href='https://codepen.io/ash_s_west/pen/abZYvrq'&gt;{title}&lt;/a&gt; by Ashley Saleem-West
      (&lt;a href='https://codepen.io/ash_s_west'&gt;@ash_s_west&lt;/a&gt;) on &lt;a href='https://codepen.io'&gt;CodePen&lt;/a&gt;.
    </iframe>
  }

  const modalList = [
    {title:"Pure-CSS-pulsing-button-effect",url:"https://codepen.io/ash_s_west/embed/GRZbvym?height=520&theme-id=dark&default-tab=css,result"},
    {title:"second",url:"https://codepen.io/ash_s_west/embed/abZYvrq?height=265&theme-id=dark&default-tab=css,result"},
    {title:"third"},
  ]


    return <div id="codepens">

      {modalList.map((item,index) => 
        <div key={index}>
          <Modal info={item} content={iFrame}/>
        </div>)
      }

      <script defer src="/static/scripts/modelIframe.js"></script>

    </div>
}

