import Modal from "../../smallComponents/Modal";

export default function CodePen() {

  function iFrame (info) { return <iframe 
      style={{width: '70vw', maxWidth: '1000px', height: '70vh'}} scrolling="no" title={info.title} 
      src={url+"?height=265&theme-id=dark&default-tab=css,result"} loading="lazy" allowTransparency="true" allowFullScreen="true" height={565} frameBorder="no">
      See the Pen &lt;a href={url}&gt;Pure CSS dark mode toggle switcher&lt;/a&gt; by Ashley Saleem-West
      (&lt;a href='https://codepen.io/ash_s_west'&gt;@ash_s_west&lt;/a&gt;) on &lt;a href='https://codepen.io'&gt;CodePen&lt;/a&gt;.
    </iframe>
  }

  function modalTrigger (img, id, title) { return <div
      className={id+"-card modal-card hover-grow"}
      title={title}
      style={{backgroundImage: `url(${img})`}}
    ></div>
  }

  const modalList = [
    {id:"codepen1",title:"second",url:"https://codepen.io/ash_s_west/pen/GRZbvym",img:"/static/mainpage/codepen-1.png"},
    {id:"codepen2",title:"Pure CSS pulsing button effect",url:"https://codepen.io/ash_s_west/pen/RwRejrb",img:"/static/mainpage/codepen-1.png"},
    {id:"codepen3",title:"second",url:"https://codepen.io/ash_s_west/pen/GRZbvym",img:"/static/mainpage/codepen-1.png"},
    {id:"codepen4",title:"Pure CSS pulsing button effect",url:"https://codepen.io/ash_s_west/pen/RwRejrb",img:"/static/mainpage/codepen-1.png"},
  ]

    return <div id="codepens">

    <h3 className="center">CodePens</h3>

    <div className="flex-gallery row">

      {modalList.map((item,index) => 
        <div key={index}>
          <Modal info={item} content={iFrame} trigger={modalTrigger}/>
        </div>)
      }

    </div>






      {/* <script defer src="/static/scripts/modelIframe.js"></script> */}

    </div>
}
