import Link from 'next/Link'
import fs from 'fs'
import path from 'path';
import Layout from './components/Layout';
import matter from 'gray-matter'; //parses metadata from markdown
import marked from 'marked'; //turns markdown into html


const Blog = ({slugs}) => {
  return (
    <div><p className="test">tests</p>
      <ul>
        {slugs[path].map( slug => {
          return(
          <li key={slug}>
            <Link as={'/blog/'+slug} href={'/blog/'+slug}>
              <a>{slug}</a>
            </Link>
          </li>
          )
        })}
      </ul>
      {/* <BlogPosts/> */}
    </div> 
  )
}

export default Layout(Blog) // This adds the global layout (header & footer ect)



const formatPost = async ({params: {slug}}) => {

  const markdownWithMeta = fs.readFileSync(path.join('posts', slug+'.md')).toString();

  const parsedMarkdown = matter(markdownWithMeta) // meta is also known as 'frontmatter'

  const htmlString = marked(parsedMarkdown.content)

  const path = slug.toString();

  return {
      props: {
          path,
          htmlString,
          data: parsedMarkdown.data
      }
  }
}

export const getStaticProps = async () => {
  const posts = fs.readdirSync('posts')

  return {
    props: { 
      slugs: posts.map(filename => formatPost(filename))
    }
  }
}



