import Link from 'next/Link'
import fs from 'fs'
import Layout from './components/Layout';
// import './main.css';

const Home = ({slugs}) => {
  return (
    <div><p className="test">test</p>
      <ul>
        {slugs.map( slug => {
          return(
          <li key={slug}>
            <Link as={'/blog/'+slug} href={'/blog/'+slug}><a>{slug}</a></Link>
          </li>
          )
        })}
      </ul>
    </div> 
  )
}

export default Layout(Home) // This adds the global layout (header & footer ect)

export const getStaticProps = async () => {
  const files = fs.readdirSync('posts')

  return {
    props: { 
      slugs: files.map(filename => filename.replace('.md',''))
    }
  }
}
